// src/components/Header.js
import React, { useEffect, useRef } from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import anime from 'animejs';

const Header = () => {
    const headerRef = useRef(null);

    useEffect(() => {
        anime({
            targets: headerRef.current,
            opacity: [0, 1],
            translateY: [-20, 0],
            easing: 'easeOutExpo',
            duration: 800,
        });
    }, []);

    return (
        <AppBar position="static" ref={headerRef} sx={{ boxShadow: 3, bgcolor: 'background.default' }}>
            <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
                    Uplift
                </Typography>
                <Box sx={{ display: 'flex', gap: 2 }}>
                    <Button color="inherit" href="/" sx={{ '&:hover': { color: 'primary.main', borderBottom: '2px solid', borderColor: 'primary.main' } }}>
                        Home
                    </Button>
                    <Button color="inherit" href="/articles" sx={{ '&:hover': { color: 'primary.main', borderBottom: '2px solid', borderColor: 'primary.main' } }}>
                        Articles
                    </Button>
                    <Button color="inherit" href="/doctors" sx={{ '&:hover': { color: 'primary.main', borderBottom: '2px solid', borderColor: 'primary.main' } }}>
                        Doctors
                    </Button>
                    <Button color="inherit" href="/login" sx={{ '&:hover': { color: 'primary.main', borderBottom: '2px solid', borderColor: 'primary.main' } }}>
                        Login
                    </Button>
                    <Button color="inherit" href="/signup" sx={{ '&:hover': { color: 'primary.main', borderBottom: '2px solid', borderColor: 'primary.main' } }}>
                        Signup
                    </Button>
                </Box>
            </Toolbar>
        </AppBar>
    );
};

export default Header;
